mapper.update:
UPDATE workflow
name = #{name}, description = #{description}, status = #{status}, version = #{version}, graph = #{graph},
WHERE id = #{id} AND owner_user_id = #{ownerUserId}

UPDATE workflow
SET status = #{status}
WHERE id = #{id} AND owner_user_id = #{ownerUserId}

mapper.select:
SELECT COUNT(1)
FROM workflow

    owner_user_id = #{ownerUserId}

        AND (name LIKE CONCAT('%', #{keyword}, '%')
        OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND status = #{status}

SELECT *
FROM workflow

    owner_user_id = #{ownerUserId}

        AND (name LIKE CONCAT('%', #{keyword}, '%')
        OR description LIKE CONCAT('%', #{keyword}, '%'))


        AND status = #{status}


ORDER BY updated_at DESC
LIMIT #{limit} OFFSET #{offset}
